mapper.select:
		select tar.*,ta.apply_title applyTitle from tb_apply_review tar
		left join tb_apply ta
		on tar.apply_id = ta.id
		where 1 = 1

			 and tar.`apply_id` = #{applyId}
			 and ta.`apply_title` = #{applyTitle}
			 and tar.`apply_review_content` = #{applyReviewContent}
			 and tar.`show_status` = #{showStatus}
			 and tar.`audit_status` = #{auditStatus}
			 and tar.`audit_msg` = #{auditMsg}
			 and tar.`apply_review_type` = #{applyReviewType}
			 and tar.`video_link` = #{videoLink}
			 and tar.`ctime` = #{ctime}
			 and tar.`is_del` = #{isDel}


			and (tar.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




                order by ${sidx} ${order}


                order by tar.id desc



			limit #{offset}, #{limit}

select count(id) from tb_apply_review where 1 = 1
	 and `apply_id` = #{applyId}
	 and `apply_review_content` = #{applyReviewContent}
	 and `show_status` = #{showStatus}
	 and `audit_status` = #{auditStatus}
	 and `audit_msg` = #{auditMsg}
	 and `apply_review_type` = #{applyReviewType}
	 and `video_link` = #{videoLink}
	 and `ctime` = #{ctime}
	 and `is_del` = #{isDel}


	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))

select 	tar.apply_review_content,
tar.video_link AS reviewVideoLink,
tar.apply_review_type
from tb_apply_review tar
where 1 = 1
 and `apply_id` = #{applyId}
 and `apply_review_content` = #{applyReviewContent}
 and `show_status` = #{showStatus}
 and `audit_status` = #{auditStatus}
 and `audit_msg` = #{auditMsg}
 and `apply_review_type` = #{applyReviewType}
 and `video_link` = #{videoLink}
 and `ctime` = #{ctime}
 and `is_del` = #{isDel}


	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}
