mapper.select:
select * from tb_card_access_record where 1 = 1

 and `card_id` = #{cardId}
 and `openid` = #{openid}
 and `card_openid` = #{cardOpenid}
 and `portrait` = #{portrait}
 and `position` = #{position}
 and `company` = #{company}
 and card_openid in (SELECT openid FROM tb_member WHERE superior_id = #{superiorId} or id = #{superiorId})
 and card_openid in (SELECT openid from tb_dept_member WHERE dept_id = #{deptId})


	and (access_time between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by access_time desc



	limit #{offset}, #{limit}

select count(id) from tb_card_access_record where 1 = 1
 and `card_id` = #{cardId}
 and `openid` = #{openid}
 and `card_openid` = #{cardOpenid}
 and `portrait` = #{portrait}
 and `position` = #{position}
 and `company` = #{company}
 and card_openid in (SELECT openid FROM tb_member WHERE superior_id = #{superiorId} or id = #{superiorId})
 and card_openid in (SELECT openid from tb_dept_member WHERE dept_id = #{deptId})


	and (access_time between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))

select tcar.*,tc.name as intervieweeName,tm.mobile  from tb_card_access_record tcar
left join tb_card tc
on tc.id =tcar.card_id
left join tb_member tm
on tcar.openid = tm.openid
where 1 = 1

 and tcar.`card_id` = #{cardId}
 and tcar.`openid` = #{openid}
 and tcar.`card_openid` = #{cardOpenid}
 and tcar.`portrait` = #{portrait}
 and tcar.`position` = #{position}
 and tcar.`company` = #{company}


	and (tcar.access_time between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by tcar.id desc



	limit #{offset}, #{limit}
