mapper.select:

SELECT
	*,
	(SELECT real_name from tb_member WHERE id = share_member_id) shareRealName,
	(SELECT title from tb_case WHERE id = case_id) caseTitle,
	(SELECT real_name from tb_member WHERE id = (SELECT member_id FROM tb_case WHERE id = case_id)) creationRealName
FROM
	tb_case_browse
WHERE
	1 = 1
 and `case_id` = #{caseId}
 and `browse_openid` = #{browseOpenid}
 and `browse_company` = #{browseCompany}
 and `browse_name` = #{browseName}
 and `share_member_id` = #{shareMemberId}
 and share_member_id in (SELECT id from tb_member where superior_id = #{superiorId} or id = #{superiorId})
 and share_member_id in (SELECT member_id from tb_dept_member WHERE dept_id = #{deptId})


	and (browse_time between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by browse_time desc



	limit #{offset}, #{limit}

select count(tcb.id) from tb_case_browse tcb
left join tb_member tm
on tcb.share_member_id = tm.id
where 1 = 1
 and share_member_id in (SELECT member_id from tb_dept_member WHERE dept_id = #{deptId})

 and tm.real_name = #{shareRealName}
 and tcb.`case_id` = #{caseId}
 and tcb.`browse_openid` = #{browseOpenid}
 and tcb.`browse_company` = #{browseCompany}
 and tcb.`browse_name` = #{browseName}
 and tcb.`share_member_id` = #{shareMemberId}
 and tcb.share_member_id in (SELECT id from tb_member where superior_id = #{superiorId} or id = #{superiorId})


	and (tcb.browse_time between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))

SELECT
*,
tm.real_name as shareRealName,
(SELECT title from tb_case WHERE id = case_id) caseTitle,
(SELECT real_name from tb_member WHERE id = (SELECT member_id FROM tb_case WHERE id = case_id)) creationRealName
FROM
tb_case_browse tcb
left join tb_member tm
on tcb.share_member_id = tm.id
WHERE
1 = 1
 and tm.real_name = #{shareRealName}
 and `case_id` = #{caseId}
 and `browse_openid` = #{browseOpenid}
 and `browse_company` = #{browseCompany}
 and `browse_name` = #{browseName}
 and `share_member_id` = #{shareMemberId}
 and share_member_id in (SELECT id from tb_member where superior_id = #{superiorId} or id = #{superiorId})


	and (browse_time between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by browse_time desc



	limit #{offset}, #{limit}
