SELECT c.id, c.title, c.view_cnt, c.member_id, c.superior_id, c.ctime, c.utime, c.intro, (SELECT count(id) from tb_case_share WHERE case_id = c.id) shareCnt, (SELECT count(id) from tb_case_praise WHERE case_id = c.id) praiseCnt, (SELECT real_name from tb_member WHERE id = c.member_id) realName, (SELECT count(id) from tb_case_browse WHERE case_id = c.id and share_member_id = #{memberId}) browseCnt (SELECT count(id) from tb_case_browse WHERE case_id = c.id) browseCnt (SELECT count(id) from tb_case_browse WHERE case_id = c.id and share_member_id in (SELECT member_id from tb_dept_member WHERE dept_id = #{deptId})) browseCnt
FROM tb_case c WHERE c.is_del = 'f' and c.id in (SELECT case_id from tb_case_share WHERE share_member_id = #{memberId}) and c.id in (SELECT case_id from tb_case_share WHERE share_superior_id = #{shareSuperiorId}) and c.id in (SELECT case_id from tb_case_share WHERE share_member_id in (SELECT member_id FROM tb_dept_member WHERE dept_id = #{deptId}))
order by ${sidx} ${order}
order by c.id desc
limit #{offset}, #{limit}

SELECT c.id, c.title, c.view_cnt, c.member_id, c.superior_id, c.ctime, c.utime, c.intro, (SELECT count(id) from tb_case_share WHERE case_id = c.id) shareCnt, (SELECT count(id) from tb_case_praise WHERE case_id = c.id) praiseCnt, (SELECT count(id) from tb_case_browse WHERE case_id = c.id) browseCnt, (SELECT real_name from tb_member WHERE id = c.member_id) realName FROM tb_case c WHERE c.is_del = 'f'
and c.member_id = #{memberId} and c.superior_id = #{superiorId}
order by ${sidx} ${order}
order by c.id desc
limit #{offset}, #{limit}

select id, title, view_cnt, member_id, superior_id, ctime, utime, intro from tb_case where is_del = 'f'

 and `title` = #{title}
 and `member_id` = #{memberId}
 and `superior_id` = #{superiorId}


	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(tc.id) from tb_case tc,tb_member tm
where tc.is_del = 'f'
and tc.member_id = tm.id and tm.bind_status='bind'
 and tc.id = #{id}
 and tm.`company` like concat('%',#{company},'%')
 and tm.mobile =#{mobile} 
 and tc.`title` = #{title}
 and tc.`member_id` = #{memberId}
 and tc.`superior_id` = #{superiorId}


	and (tc.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))

select tc.id,tc.title,tc.view_cnt,tc.ctime,tc.utime,tc.member_id,tm.nickname as memberName,tm.company as company,tm.mobile as mobile
from tb_case tc,tb_member tm
where tc.is_del = 'f'
and tc.member_id = tm.id and tm.bind_status='bind'
 and tc.title = #{title}
 and tc.id = #{id}
 and tm.`company` like concat('%',#{company},'%')
 and tm.mobile =#{mobile} 
 and tc.member_id = #{memberId}
 and tc.superior_id = #{superiorId}


	and (tc.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by tc.id desc



	limit #{offset}, #{limit}
