mapper.select:

SELECT
	*,
	(SELECT real_name from tb_member WHERE id = share_member_id) shareRealName,
	(SELECT title from tb_case WHERE id = case_id) caseTitle,
	(SELECT real_name from tb_member WHERE id = (SELECT member_id FROM tb_case WHERE id = case_id)) creationRealName
FROM
	tb_case_leave
WHERE
	is_del = 'f'

 and `content` = #{content}
 and `openid` = #{openid}
 and `name` = #{name}
 and `mobile` = #{mobile}
 and `status` = #{status}
 and `case_id` = #{caseId}
 and `share_member_id` = #{shareMemberId}
 and share_member_id in (SELECT id from tb_member where superior_id = #{superiorId} or id = #{superiorId})
 and share_member_id in (SELECT member_id from tb_dept_member WHERE dept_id = #{deptId})


	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(id) from tb_case_leave where is_del = 'f'
 and `content` = #{content}
 and `openid` = #{openid}
 and `name` = #{name}
 and `mobile` = #{mobile}
 and `status` = #{status}
 and `case_id` = #{caseId}
 and `share_member_id` = #{shareMemberId}
 and share_member_id in (SELECT id from tb_member where superior_id = #{superiorId} or id = #{superiorId})
 and share_member_id in (SELECT member_id from tb_dept_member WHERE dept_id = #{deptId})


	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))

SELECT
*,
(SELECT real_name from tb_member WHERE id = share_member_id) shareRealName
FROM
tb_case_leave
