is_del = 'f'

 and `content` = #{content}
 and `openid` = #{openid}
 and `name` = #{name}
 and `mobile` = #{mobile}
 and `status` = #{status}
 and `case_id` = #{caseId}
 and `share_member_id` = #{shareMemberId}


	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}
