mapper.select:
select * from tb_clientele_allot_record where 1 = 1

 and `clientele_id` = #{clienteleId}
 and `operation_member` = #{operationMember}
 and `source_member` = #{sourceMember}
 and `target_member` = #{targetMember}


	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(tcar.id) from tb_clientele_allot_record tcar
LEFT JOIN ( SELECT id, nickname FROM tb_member ) tto ON tto.id = tcar.operation_member
where 1 = 1
 and tto.nickname = #{operationName}
 and tcar.`clientele_id` = #{clienteleId}
 and tcar.`operation_member` = #{operationMember}
 and tcar.`source_member` = #{sourceMember}
 and tcar.`target_member` = #{targetMember}


	and (tcar.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))

SELECT
ts.nickname AS sourceName,
tn.nickname AS targetName,
tto.nickname AS operationName,
tc.`name` as clienteleName,
tcar.*
FROM
tb_clientele_allot_record tcar
LEFT JOIN ( SELECT id, nickname FROM tb_member ) ts ON tcar.source_member = ts.id
LEFT JOIN ( SELECT id, nickname FROM tb_member ) tn ON tn.id = tcar.target_member
LEFT JOIN ( SELECT id, nickname FROM tb_member ) tto ON tto.id = tcar.operation_member
LEFT JOIN tb_clientele tc on tcar.clientele_id = tc.id

where 1 = 1

 and tto.nickname = #{operationName}
 and tcar.`clientele_id` = #{clienteleId}
 and tcar.`operation_member` = #{operationMember}
 and tcar.`source_member` = #{sourceMember}
 and tcar.`target_member` = #{targetMember}


	and (tcar.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by tcar.id desc



	limit #{offset}, #{limit}
