mapper.select:
		SELECT DISTINCT c.*, ct.clientele_id, (SELECT real_name from tb_member m where m.id = c.member_id) memberName from tb_clientele c LEFT JOIN tb_clientele_tag ct on c.id = ct.clientele_id
		where c.is_del = 'f'

		 and c.member_id = #{memberId}
		 and c.superior_id = #{superiorId}


			and (c.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))



			and (c.name LIKE concat('%', #{key}, '%') or c.mobile LIKE concat('%',#{key},'%'))



			and c.member_id in (SELECT member_id from tb_dept_member WHERE dept_id = #{deptId})



			and ct.tag_id in

                #{item}





				order by c.${sidx} ${order}


				order by c.id desc




			limit #{offset}, #{limit}

select * from tb_clientele where is_del = 'f'

 and `name` = #{name}
 and `mobile` = #{mobile}
 and `portrait` = #{portrait}
 and `company` = #{company}
 and `position` = #{position}
 and `member_id` = #{memberId}


	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(tc.id) from tb_clientele tc
left join tb_member tm
on tc.member_id = tm.id
where tc.is_del = 'f'
 and tc.`source` = #{source}
 and tm.`nickName` = #{functionaryName}

	and member_id in (SELECT member_id from tb_dept_member WHERE dept_id = #{deptId})


 and tc.`name` = #{name}
 and tc.`mobile` = #{mobile}
 and tc.`portrait` = #{portrait}
 and tc.`company` = #{company}
 and tc.`position` = #{position}
 and tc.`superior_id` = #{superiorId}
 and tc.`member_id` = #{memberId}


	and (tc.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))

select tc.*,tm.nickName as functionaryName,tcr.total as tcrtotal,tcl.total as tcltotal from tb_clientele tc
left join tb_member tm
on tc.member_id = tm.id
LEFT JOIN
(SELECT tcr.clientele_id as cid,count(tcr.id) total FROM tb_clientele_remark tcr GROUP BY tcr.clientele_id) tcr
on tc.id = tcr.cid
LEFT JOIN
(SELECT tcl.clientele_id as cid,count(tcl.id) total FROM tb_clientele_tag tcl GROUP BY tcl.clientele_id) tcl
on tc.id = tcl.cid
where tc.is_del = 'f'
 and tm.`nickName` = #{functionaryName}
 and tc.`name` = #{name}
 and tc.`source` = #{source}
 and tc.`mobile` = #{mobile}
 and tc.`portrait` = #{portrait}
 and tc.`company` = #{company}
 and tc`position` = #{position}
 and tc.`member_id` = #{memberId}


	and (tc.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by tc.id desc



	limit #{offset}, #{limit}
