mapper.select:
select * from tb_clientele_remark where 1 = 1

 and `member_id` = #{memberId}
 and `content` = #{content}
 and `clientele_id` = #{clienteleId}


	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(tcr.id) from tb_clientele_remark tcr
left join tb_member tb
on tcr.member_id = tb.id
 where 1 = 1
 and tcr.`member_id` = #{memberId}
 and tcr.`content` = #{content}
 and tcr.`clientele_id` = #{clienteleId}
 and tcr.`id` = #{id}
 and tb.`mobile` = #{mobile}

	and tb.`company` like concat('%',#{company},'%')


	and (tcr.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))

select tcr.*,tb.nickname as createName,tb.company as createCompany,tb.mobile as createMobile,
tc.name as clienteleName,tc.mobile as clienteleMobile
from tb_clientele_remark tcr
left join tb_member tb
on tcr.member_id = tb.id
left join tb_clientele tc
on tcr.clientele_id = tc.id
where 1 = 1
 and tcr.`id` = #{id}
 and tb.`mobile` = #{mobile}

	and tb.`company` like concat('%',#{company},'%')

 and tcr.`member_id` = #{memberId}
 and tcr.`content` = #{content}
 and tcr.`clientele_id` = #{clienteleId}


	and (tcr.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by tcr.id desc



	limit #{offset}, #{limit}
