mapper.select:
select * from tb_clientele_tag where 1 = 1

 and `clientele_id` = #{clienteleId}
 and `member_id` = #{memberId}
 and `tag_id` = #{tagId}



		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(tct.id) from tb_clientele_tag tct
left join tb_member tm
on tct.member_id = tm.id
 where 1 = 1
 and tct.`id` = #{id}
 and tm.`mobile` = #{mobile}

	and tm.`company` like concat('%',#{company},'%')

 and tct.`clientele_id` = #{clienteleId}
 and tct.`member_id` = #{memberId}
 and tct.`tag_id` = #{tagId}

select tct.*,tm.nickname as createName,
tm.mobile as createMobile,tm.company as createCompany,
tt.name as tagName,tc.name as clienteleName
from tb_clientele_tag tct
left join tb_member tm
on tct.member_id = tm.id
left join tb_tag tt
on tt.id =tct.tag_id
left join tb_clientele tc
on tct.clientele_id = tc.id
where 1=1
 and tct.`id` = #{id}
 and tm.`mobile` = #{mobile}

	and tm.`company` like concat('%',#{company},'%')

 and tct.`clientele_id` = #{clienteleId}
 and tct.`member_id` = #{memberId}

 and tct.`tag_id` = #{tagId}



		order by ${sidx} ${order}


		order by tct.id desc



	limit #{offset}, #{limit}
