mapper.select:
SELECT
	*,
	(SELECT count(member_id) from tb_dept_member dm WHERE dm.dept_id = d.id) teamCnt
FROM
	tb_dept d
WHERE
	d.is_del = 'f'
 and d.`name` = #{name}
 and d.`superior_id` = #{superiorId}



		order by ${sidx} ${order}


		order by d.id desc



	limit #{offset}, #{limit}

select count(td.id) from tb_dept td
left join tb_member tm
on td.superior_id = tm.id
where td.is_del = 'f'
 and tm.company = #{company}
 and td.`name` = #{name}
 and td.`superior_id` = #{superiorId}

SELECT
d.*,tm.nickname as superiorName,
tm.company as company,
(SELECT count(member_id) from tb_dept_member dm WHERE dm.dept_id = d.id) teamCnt
FROM
tb_dept d
left join tb_member tm
on d.superior_id = tm.id
WHERE
d.is_del = 'f'
 and tm.company = #{company}
 and d.`name` = #{name}
 and d.`superior_id` = #{superiorId}



		order by ${sidx} ${order}


		order by d.id desc



	limit #{offset}, #{limit}
