mapper.select:
		select * from tb_information where 1 = 1

			 and `title` = #{title}
			 and `video_link` = #{videoLink}
			 and `content` = #{content}
			 and `audit_status` = #{auditStatus}
			 and `is_del` = #{isDel}
			 and `ctime` = #{ctime}

				and (title LIKE concat('%', #{keyword}, '%') or content LIKE concat('%',#{keyword},'%'))



			and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




                order by ${sidx} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(id) from tb_information where 1 = 1
	 and `title` = #{title}
	 and `video_link` = #{videoLink}
	 and `content` = #{content}
	 and `is_del` = #{isDel}
	 and `information_type` = #{informationType}

	 and `ctime` = #{ctime}
	 and `update_time` = #{updateTime}


	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))

SELECT
ti.*
FROM
tb_information ti
WHERE
1 = 1
 and ti.`title` = #{title}
 and ti.`video_link` = #{videoLink}
 and ti.`content` = #{content}
 and ti.`audit_status` = #{auditStatus}
 and ti.`is_del` = #{isDel}
 and ti.`information_type` = #{informationType}

	and (ti.title LIKE concat('%', #{keyword}, '%') or ti.content LIKE concat('%',#{keyword},'%'))



	and (ti.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by ti.id desc



	limit #{offset}, #{limit}

select ti.*,tc.openid from tb_information ti
left join tb_collect tc
on ti.id =tc.data_id
where ti.id = #{id}
and tc.collect_type = 1
and tc.openid = #{openid}

SELECT
ti.*
FROM
tb_information ti
left join tb_collect tc
on ti.id = tc .data_id
WHERE
1 = 1
and tc.collect_type = 1
 and tc.`openid` = #{openid}
 and ti.`title` = #{title}
 and ti.`video_link` = #{videoLink}
 and ti.`content` = #{content}
 and ti.`audit_status` = #{auditStatus}
 and ti.`is_del` = #{isDel}
 and ti.`information_type` = #{informationType}

	and (ti.title LIKE concat('%', #{keyword}, '%') or ti.content LIKE concat('%',#{keyword},'%'))



	and (ti.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by ti.id desc



	limit #{offset}, #{limit}
