mapper.select:
SELECT *, (SELECT real_name from tb_member where id = member_id) realName FROM tb_leave WHERE is_del = 'f' and `member_id` = #{memberId} and member_id in (SELECT id from tb_member where superior_id = #{superiorId} or id = #{superiorId})
and member_id in (SELECT member_id from tb_dept_member WHERE dept_id = #{deptId})
and (name LIKE concat('%', #{key}, '%') or mobile LIKE concat('%',#{key},'%'))
order by ${sidx} ${order}
order by id desc
limit #{offset}, #{limit}

select * from tb_leave where is_del = 'f'

 and `content` = #{content}
 and `openid` = #{openid}
 and `portrait` = #{portrait}
 and `name` = #{name}
 and `mobile` = #{mobile}
 and `status` = #{status}
 and `member_id` = #{memberId}

	and member_id in (SELECT member_id from tb_dept_member WHERE dept_id = #{deptId})



	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(tl.id) from tb_leave tl
left join tb_member tm
on tl.member_id = tm.id
 where tl.is_del = 'f'

	and tm.`company` like concat('%',#{company},'%')

 and tl.`content` = #{content}
 and tl.`openid` = #{openid}
 and tl.`portrait` = #{portrait}
 and tl.`name` = #{name}
 and tl.`mobile` = #{mobile}
 and tl.`status` = #{status}
 and tl.`member_id` = #{memberId}
 and tl.member_id in (SELECT id from tb_member where superior_id = #{superiorId} or id = #{superiorId})

	and member_id in (SELECT member_id from tb_dept_member WHERE dept_id = #{deptId})



	and (tl.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))

select tl.*,tm.nickname as toName,tm.company as toCompany from tb_leave tl
left join tb_member tm
on tl.member_id = tm.id
where tl.is_del = 'f'
 and tl.`id` = #{id}
 and tl.`content` = #{content}
 and tl`openid` = #{openid}
 and tl.`portrait` = #{portrait}
 and tl.`name` = #{name}

 and tm.`company` like concat('%',#{company},'%')

 and tl.`mobile` = #{mobile}
 and tl.`status` = #{status}
 and tl.`member_id` = #{memberId}


	and (tl.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by tl.id desc



	limit #{offset}, #{limit}
