mapper.select:
SELECT * FROM tb_member m WHERE m.role = 'staff' AND m.is_del = 'f' AND m.STATUS = 'normal' AND m.auth_status = 't' AND m.bind_status = 'bind' AND m.superior_id = #{superiorId} AND m.id NOT IN ( SELECT member_id FROM tb_dept_member )
and (m.real_name LIKE concat('%', #{key}, '%') or m.mobile LIKE concat('%',#{key},'%'))
order by ${sidx} ${order}
order by m.id DESC
limit #{offset}, #{limit}

SELECT m.*, dm.dept_id, dm.member_id, dm.perm, dm.join_time FROM tb_member m INNER JOIN tb_dept_member dm ON m.id = dm.member_id WHERE m.role = 'staff' AND m.is_del = 'f' AND m.STATUS = 'normal' AND m.auth_status = 't' AND m.bind_status = 'bind' AND dm.dept_id = #{deptId}
and (m.real_name LIKE concat('%', #{key}, '%') or m.mobile LIKE concat('%',#{key},'%'))
order by ${sidx} ${order}
order by dm.perm DESC, dm.join_time DESC
limit #{offset}, #{limit}

SELECT m.*, dm.dept_id, dm.perm, dm.join_time from tb_member m LEFT JOIN tb_dept_member dm on m.id = dm.member_id where m.is_del = 'f' and m.auth_status = 't' and m.bind_status = 'bind' and (m.superior_id = #{superiorId} or m.id = #{superiorId}) and m.id in (SELECT member_id from tb_dept_member WHERE dept_id = #{deptId})
and (m.real_name LIKE concat('%', #{key}, '%') or m.mobile LIKE concat('%',#{key},'%'))
order by ${sidx} ${order}
order by m.id desc
limit #{offset}, #{limit}

SELECT m.*, dm.dept_id, dm.perm, dm.join_time, (SELECT name from tb_dept d WHERE d.id = (SELECT dm.dept_id from tb_dept_member dm WHERE dm.member_id = m.id)) deptName FROM tb_member m LEFT JOIN tb_dept_member dm on m.id = dm.member_id WHERE m.is_del = 'f' and m.superior_id = #{superiorId} and m.id in (SELECT member_id from tb_dept_member WHERE dept_id = #{deptId}) and m.auth_status = #{authStatus} and m.bind_status = #{bindStatus}
and (m.real_name LIKE concat('%', #{key}, '%') or m.mobile LIKE concat('%',#{key},'%'))
order by ${sidx} ${order}
order by m.id desc
limit #{offset}, #{limit}

select * from tb_member where is_del = 'f'
 and `id` = #{id}
 and `portrait` = #{portrait}
 and `nickname` = #{nickname}
 and `gender` = #{gender}
 and `role` = #{role}
 and `status` = #{status}
 and `superior_id` = #{superiorId}
 and `real_name` = #{realName}
 and `mobile` = #{mobile}
 and `openid` = #{openid}
 and `cert_type` = #{certType}
 and `type` = #{type}
 and `auth_status` = #{authStatus}


	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(m.id) from tb_member m where m.is_del = 'f'
 and m.`portrait` = #{portrait}
 and m.`company` = #{company}
 and m.`nickname` = #{nickname}
 and m.`gender` = #{gender}
 and m.`role` = #{role}
 and m.`status` = #{status}
 and `superior_id` = #{superiorId}
 and m.`real_name` = #{realName}
 and m.`mobile` = #{mobile}
 and m.`openid` = #{openid}
 and m.`cert_type` = #{certType}
 and m.`type` = #{type}
 and (m.`code` !=null or m.`code` !='')
 and m.`auth_status` = #{authStatus}
 and m.`bind_status` = #{bindStatus}


	and (m.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))

select
m2.nickname as memberBossName,
tc.company,
m.*
FROM
tb_member m
LEFT JOIN tb_member m2 ON m.superior_id = m2.id
LEFT JOIN tb_card tc ON m.openid = tc.openid
where m.is_del = 'f' and m.superior_id is not null and m.bind_status = 'bind'
 and m.`id` = #{id}
 and m.`portrait` = #{portrait}
 and m.`nickname` = #{nickname}
 and m.`gender` = #{gender}
 and m.`role` = #{role}
 and m.`status` = #{status}
 and (m.`code` !=null or m.`code` !='')
 and m.`superior_id` =
	(select s.id from tb_member s
	where s.nickname =
	#{superiorId})

 and m.`real_name` = #{realName}
 and m.`mobile` = #{mobile}
 and m.`openid` = #{openid}
 and m.`cert_type` = #{certType}
 and m.`type` = #{type}
 and m.`auth_status` = #{authStatus}
 and tc.`company` = #{company}

	and (m.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by m.id desc



	limit #{offset}, #{limit}

