	 and `portrait` = #{portrait}
	 and `nickname` = #{nickname}
	 and `gender` = #{gender}
	 and `ctime` = #{ctime}
	 and `is_del` = #{isDel}
	 and `status` = #{status}
	 and `real_name` = #{realName}
	 and `mobile` = #{mobile}
	 and `openid` = #{openid}
	 and `type` = #{type}
	 and `company` = #{company}
	 and `email` = #{email}


	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))

select t.*
FROM tb_member t
,tb_member_banner tmb
WHERE t.id <> tmb.member_id
 and t.`portrait` = #{portrait}
 and t.`nickname` = #{nickname}
 and t.`gender` = #{gender}
 and t.`ctime` = #{ctime}
 and t.`is_del` = #{isDel}
 and t.`status` = #{status}
 and t.`real_name` = #{realName}
 and t.`mobile` = #{mobile}
 and t.`openid` = #{openid}
 and t.`type` = #{type}
 and t.`code` = #{code}
 and t.`email` = #{email}

	and (t.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by t.id desc



	limit #{offset}, #{limit}

select tm.* from tb_member tm
left join tb_collect tc
on tm.id = tc.data_id
where 1 = 1
 and `portrait` = #{portrait}
 and `nickname` = #{nickname}
 and `gender` = #{gender}
 and `ctime` = #{ctime}
 and `is_del` = #{isDel}
 and `status` = #{status}
 and `real_name` = #{realName}
 and `mobile` = #{mobile}
 and tc.`openid` = #{openid}
 and `type` = #{type}
 and `company` = #{company}
 and `email` = #{email}

	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}

