select * from tb_member where 1 = 1 and type ='vip' and status ='normal'
and (`nickname` like concat('%',#{keyAll},'%') or`mobile` like concat('%',#{keyAll},'%') or`code` like concat('%',#{keyAll},'%') or`phone` like concat('%',#{keyAll},'%') or`weixin_number` like concat('%',#{keyAll},'%') or`nationality` like concat('%',#{keyAll},'%') or`address` like concat('%',#{keyAll},'%') or`profile` like concat('%',#{keyAll},'%') or`company_profile` like concat('%',#{keyAll},'%') or`have_resource` like concat('%',#{keyAll},'%') or`need_resource` like concat('%',#{keyAll},'%') or`mobile_country` like concat('%',#{keyAll},'%') or`phone_country` like concat('%',#{keyAll},'%') )
and `address` = #{address} and `nationality` = #{nationality}
order by ${sidx} ${order}
order by id desc
limit #{offset}, #{limit}
