mapper.select:
select * from tb_template_website_layout where 1 = 1

 and `metadata_id` = #{metadataId}
 and `member_id` = #{memberId}
 and `openid` = #{openid}


	and (ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(ttwl.id)
from tb_template_website_layout ttwl
left join tb_member tm
on ttwl.openid = tm.openid
where 1 = 1
and tm.bind_status = 'bind'
 and tm.mobile = #{mobile}
 and ttwl.`metadata_id` = #{metadataId}
 and ttwl.`member_id` = #{memberId}
 and ttwl.`openid` = #{openid}


	and (ttwl.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))

select ttwl.*,tm.nickname as nickName,tm.company as company,tm.mobile as mobile,ss.total as useTotal
 from tb_template_website_layout ttwl
left join tb_member tm
on ttwl.openid = tm.openid
left join
(select count(ttwur.id)as total,ttwur.openid from tb_template_website_use_record ttwur group by ttwur.openid)ss
on ss.openid = ttwl.openid
where 1 = 1
and tm.bind_status = 'bind'
 and tm.mobile = #{mobile}
 and ttwl.`metadata_id` = #{metadataId}
 and ttwl.`member_id` = #{memberId}
 and ttwl.`openid` = #{openid}


	and (ttwl.ctime between CONCAT(#{sdate},' 00:00:00') and CONCAT(#{edate},' 23:59:59'))




		order by ${sidx} ${order}


		order by ttwl.id desc



	limit #{offset}, #{limit}
